void generate_row(int curRow) {
	for (int j = 0; j < COL; j++) {
		// clear all color data before setting new color values
		GPIOC->BRR = 0b111111;

		// set color?
		GPIOC->BSRR = canvas[curRow][j];// * (WHITE | (WHITE<<3));

		// Clock toggle
		GPIOC->BSRR = 1<<CLK; 	// set
		GPIOC->BRR = 1<<CLK;	// clear
	 }

	// turn on OE the LAT
	GPIOC->BSRR = 1<<OE;		//OE;
	GPIOC->BSRR = 1<<LAT;		//LAT;

	// clear the target row value and set it to current row value
	GPIOC->BRR = 0b11111<<PINA;	//SE0;
	GPIOC->BSRR = curRow<<PINA;	//SE0);

	// turn off LAT then OE
	GPIOC->BRR = 1<<LAT;		//LAT;
	GPIOC->BRR = 1<<OE;		//OE;
}

void generate_image() {
	// clear output values for pins
	GPIOC->BRR |= 0b11111111111111;

	// print the image row by row
	for (int i = 0; i < ROW; i++) {
		generate_row(i);
	}
}

void mask_canvas(uint8_t image, uint8_t color, int selRow, int selCol) {
	int imageWidth = sizeof((*image)[0]) / sizeof((*image)[0][0]);
	int imageHeight = sizeof(image) / sizeof(image[0]);

	for (int i = 0; i < imageHeight; i++) {

		// targeted values
		int tarRow = selRow + i;
		uint8_t tarColor = color;

		// mask to clear the colors
		uint8_t clearMask = 0b111;

		// check if vertically out of dimension
		if (((tarRow) < (ROW*2)) && ((tarRow) >= 0)) {

			if ((tarRow) >= ROW) {
				tarRow -= 32;
				tarColor *= 8;
				clearMask <<= 3;
			}

			for (int j = 0; j < imageWidth; j++) {

				int tarCol = selCol + j;

				// check if horizontally out of dimension
				if (((tarCol) < COL) && ((tarCol) >= 0)) {

					// clear the current canvas
					canvas[tarRow][tarCol] &= ~clearMask;

					// color the image
					if (image[i][j] > 0) {
						image[i][j] = tarColor;
					}

					// draw the image
					canvas[tarRow][tarCol] |= image[i][j];
				}
			}
		}
	}
}

void clear_display() {
	uint8_t (*matrix_ptr)[ROW][COL] = &empty;
	mask_canvas((*matrix_ptr), 0b000, 0, 0);
	mask_canvas((*matrix_ptr), 0b000, 32, 0);
}

void draw_arrow(int selRow){
	mask_canvas(sample, RED, selRow, 20);
	generate_image();
}
