void generate_row(int curRow) {
    for (int j = 0; j < COL; j++) {
        // clear all color data before setting new color values
        GPIOC->BRR = 0b111111;

        // set color?
        GPIOC->BSRR = canvas[curRow][j];// * (WHITE | (WHITE<<3));

        // Clock toggle
        GPIOC->BSRR = 1<<CLK; 	// set
        GPIOC->BRR = 1<<CLK;	// clear
    }

    // turn on OE the LAT
    GPIOC->BSRR = 1<<OE;		//OE;
    GPIOC->BSRR = 1<<LAT;		//LAT;

    // clear the target row value and set it to current row value
    GPIOC->BRR = 0b11111<<PINA;	//SE0;
    GPIOC->BSRR = curRow<<PINA;	//SE0);

    // turn off LAT then OE
    GPIOC->BRR = 1<<LAT;		//LAT;
    GPIOC->BRR = 1<<OE;		//OE;
}

void generate_image() {
    // clear output values for pins
    GPIOC->BRR |= 0b11111111111111;

    // print the image row by row
    for (int i = 0; i < ROW; i++) {
        generate_row(i);
    }
}

void mask_canvas(uint8_t color, int selRow, int selCol, int imageHeight, int imageWidth, uint8_t (*imgptr)[imageWidth]) {
    //int imageWidth = (int)sizeof(imgptr[0]) / sizeof(imgptr[0][0]);
    //int imageHeight = sizeof(imgptr) / sizeof(imgptr[0]);

    for (int i = 0; i < imageHeight; i++) {

        // targeted values
        int tarRow = selRow + i;
        uint8_t tarColor = color;

        // mask to clear the colors
        uint8_t clearMask = 0b111;

        // check if vertically out of dimension
        if (((tarRow) < (ROW*2)) && ((tarRow) >= 0)) {

            if ((tarRow) >= ROW) {
                tarRow -= ROW;
                tarColor <<= 3;
                clearMask <<= 3;
            }

            for (int j = 0; j < imageWidth; j++) {

                int tarCol = selCol + j;

                // check if horizontally out of dimension
                if (((tarCol) < COL) && ((tarCol) >= 0)) {

                    // clear the current canvas
                    canvas[tarRow][tarCol] &= ~clearMask;

                    // color the image
                    if (imgptr[i][j] > 0) {
                        imgptr[i][j] = tarColor;
                    }

                    // draw the image
                    canvas[tarRow][tarCol] = imgptr[i][j];
                }
            }
        }
    }
}

void clear_display() {
    int row = (int)sizeof(empty) / sizeof(empty[0]);
    int width = (int)sizeof(empty[0]) / sizeof(empty[0][0]);
    mask_canvas(0b000, 0, 0, row, width, empty);
    mask_canvas(0b000, 32, 0, row, width, empty);
}

void draw_arrow(int selRow){
    int row = (int)sizeof(sample) / sizeof(sample[0]);
    int width = (int)sizeof(sample[0]) / sizeof(sample[0][0]);
    mask_canvas(RED, 10, selRow, row, width, sample);
    generate_image();
}
